import React , { Component } from 'react';
import { Button, Container, Row, Col } from 'react-bootstrap';

export default (props) => {
    const { topping, onClick } = props;
    return (
        <>
            <Container>
                <Row>
                <Button onClick={onClick.toppingPlus} variant="outline-danger">+</Button>
                <h5><b> {topping} </b></h5>
                
                <Button onClick={onClick.toppingMinus} variant="outline-danger">-</Button>
                </Row>
            </Container>
        </>
    )  
}
