import React , { Component } from 'react';
import { Row, Container, Col } from 'react-bootstrap';
import { Route, Switch } from "react-router-dom";

import DecideTopping from './DecideTopping';
import LRLinkBtn from "./LRLinkBtn";
import DndPizza from "./DndPizza";
import CheckOut from "./CheckOut";

export default class DiyPizza extends Component {
    constructor() {
        super()
        this.state = {
            topping: 0,
            lastPage: "",
            currentPage: "topping",
            nextPage: "dnd", 
        }
    }
    // topping page function
    toppingPlus = () => {
        if (this.state.topping < 3) {
            this.setState((state) => ({
                topping: state.topping + 1
            }));
        }
    }

    toppingMinus = () => {
        if (this.state.topping > 0) {
            this.setState((state) => ({
                topping: state.topping - 1
            }));
        }
    }
    ///////////////////////////////////
    
    //LRbtn function
    
    ///////////////////////////////////

    // Collection of every route in order
    routeOrder = ["topping", "dnd", "checkout"];

    // props of every page
    toppingPageProps = {
        onClick: { toppingPlus: this.toppingPlus, toppingMinus: this.toppingMinus },
        topping: this.state.topping
    }

    dndProps = {
        topping: this.state.topping
    }

    checkOutProps = {

    }
    //////////////////////////////
    render() {
        return (
            <>
                <Row>
                <section classname="h-3" topping></section>
                </Row>
                <Row>
                    <Col>
                        <LRLinkBtn pos='L' route={this.state.lastPage}/>
                    </Col>
                    <Col>
                        <Switch>
                            <Route path="/topping" render={(toppingPageProps) => <DecideTopping {...toppingPageProps} />}/>
                            <Route path="/dnd" render={(dndProps) => <DndPizza {...dndProps} />} />
                            <Route path="/checkout" render={(checkOutProps) => <CheckOut {...checkOutProps} />} />
                        </Switch>
                    </Col>
                    <Col>
                        <LRLinkBtn pos='R' route={this.state.nextPage}/>
                    </Col>
                </Row>
                <Row>
                    <section></section>
                </Row>
            <Container>
                    
                        
            </Container>
            </>
        )
    }
}
