import React from 'react';
import { Row, ListGroup, Col, Image, Container } from 'react-bootstrap';
import { Droppable } from 'react-beautiful-dnd';

const topping = [
    { url: "../../images/mushroom.png", id: "mushroom" },
    { url: "../../images/pepperoni.png", id: "pepperoni" },
    { url: "../../images/green_pepper.png", id: "green_pepper" },    
    { url: "../../images/black_olive.png", id: "black_olive" },    
    { url: "../../images/onion.png", id: "onion" }
]    

const dragImgStyle = {
    width: '100px'
}

export default () => {
    return(
        <ListGroup style={{display: 'inline-block'}}>
            {topping.map((topping) => (
                <Droppable droppableId={topping.id}>
                {() => (
                    <ListGroup.Item>
                        <Image src={require(topping.url)} style={dragImgStyle} rounded />
                    </ListGroup.Item>
                )}
                </Droppable>
            ))}
        </ListGroup>
    )
    
}
