import React , { Component } from 'react';
import { Row, Container, Col } from 'react-bootstrap';
import { Route, Switch } from "react-router-dom";

import DecideTopping from './DecideTopping';
import LRLinkBtn from "./LRLinkBtn";
import DndPizza from "./DndPizza";
import CheckOut from "./CheckOut";

export default class DiyPizza extends Component {
    constructor() {
        super()
        this.state = {
            topping: 0,
            lastPage: "",
            currentPage: "topping",
            nextPage: "dnd", 
        }

        // props of every page
    
        this.checkOutProps = {
    
        }
    }
    // topping page function
    toppingPlus = () => {
        if (this.state.topping < 3) {
            this.setState((state) => ({
                topping: state.topping + 1
            }));
        }
    }

    toppingMinus = () => {
        if (this.state.topping > 0) {
            this.setState((state) => ({
                topping: state.topping - 1
            }));
        }
    }
    ///////////////////////////////////
    
    //LRbtn function
    btnLonClick = () => {

    }
    btnRonClick = () => {

    }
    ///////////////////////////////////

    // Collection of every route in order
    routeOrder = ["topping", "dnd", "checkout"];
    
    //////////////////////////////
    render() {
        return (
            <>
            <Container>
                <Row>
                <section classname="h-3"></section>
                </Row>
                <Row>
                    <Col className="center-text">
                        <Row className="center-text">
                        <LRLinkBtn pos='L' route={this.state.lastPage} style={{"margin-top": "200px"}}/>
                        </Row>
                    </Col>
                    <Col>
                        <Switch>
                            <Route path="/order/diy/topping" render={() => <DecideTopping onClick={{toppingPlus: this.toppingPlus, toppingMinus: this.toppingMinus}} topping={this.state.topping}/>}/>
                            <Route path="/order/diy/dnd" render={() => <DndPizza topping={this.state.topping} />} />
                            <Route path="/order/diy/checkout" render={(checkOutProps) => <CheckOut {...checkOutProps} />} />
                        </Switch>
                    </Col>
                    <Col>
                        <Row md={4}>
                        </Row>
                        <Row md={4}>
                        <LRLinkBtn pos='R' route={this.state.nextPage} style={{"margin-top": "200px"}}/>
                        </Row>
                        <Row md={4}>
                        </Row>
                    </Col>
                </Row>
                <Row>
                    <section></section>
                </Row>      
            </Container>
            </>
        )
    }
}
