import React , { Component } from 'react';
import { Row, Container, Col } from 'react-bootstrap';
import { Route, Switch, BrowserRouter } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.css';

import DecideTopping from './DecideTopping';
import LRLinkBtn from "./LRLinkBtn";
import DndPizza from "./DndPizza";
import CheckOut from "./CheckOut";

export default class DiyPizza extends Component {
    constructor() {
        super()
        this.state = {
            topping: 0,
            lastPage: "",
            currentPage: "topping",
            nextPage: "dnd", 
        }

        // props of every page
    
        this.checkOutProps = {
    
        }
    }
    // topping page function
    toppingPlus = () => {
        if (this.state.topping < 3) {
            this.setState((state) => ({
                topping: state.topping + 1
            }));
        }
    }

    toppingMinus = () => {
        if (this.state.topping > 0) {
            this.setState((state) => ({
                topping: state.topping - 1
            }));
        }
    }
    ///////////////////////////////////
    
    //LRbtn function
    btnLonClick = () => {

    }
    btnRonClick = () => {

    }
    ///////////////////////////////////

    // Collection of every route in order
    routeOrder = ["topping", "dnd", "checkout"];
    
    //////////////////////////////
    render() {
        return (
            <>
            <Container>
            <Row>
            <section className="h-3"></section>
            </Row>
            <Row>
                <Col className="center-text">
                    <Row className="center-text">
                    <LRLinkBtn pos='L' route={this.state.lastPage}/>
                    </Row>
                </Col>
                <Col>
                    <Switch>
                    <Route exact path="/order/diy/topping" render={() => <DecideTopping onClick={{toppingPlus: this.toppingPlus, toppingMinus: this.toppingMinus}} topping={this.state.topping}/>}/>
                    <Route exact path="/order/diy/dnd" render={() => <DndPizza topping={this.state.topping} />} />
                    <Route path="/order/diy/checkout" render={(checkOutProps) => <CheckOut {...checkOutProps} />} />
                    </Switch>                        
                </Col>
                    <Col sytle={{"display": "flex", "justify-content": "space-between"}}>
                    <Row sytle={{"align-items": "center"}}>
                    <LRLinkBtn pos='R' route={this.state.nextPage}/>
                    </Row>
                </Col>
            </Row>
            <Row>
                <section></section>
            </Row>      
            </Container>
            </>
        )
    }
}
