import React , { Component } from 'react';
import { Row, Container } from 'react-bootstrap';
import { DragDropContext } from 'react-beautiful-dnd';
import DecideTopping from './DecideTopping';

export default class DiyPizza extends Component {
    constructor() {
        super()
        this.state = {
            topping: 0
        }
    }

    toppingPlus = () => {
        if (this.state.topping < 3) {
            this.setState((state) => ({
                topping: state.topping + 1
            }));
        }
    }

    toppingMinus = () => {
        if (this.state.topping > 0) {
            this.setState((state) => ({
                topping: state.topping - 1
            }));
        }
    }

    onDragEnd = result => {

    }

    render() {
        return (
            <>
            <Container>
                    <Row>
                        <section classname="h-3" topping></section>
                    </Row>
                    <DecideTopping onClick={{ toppingPlus: this.toppingPlus, toppingMinus: this.toppingMinus }} topping={this.state.topping} />
                    <Row>
                        <section></section>
                    </Row>
            </Container>
            </>
        )
    }
}
